From clutch.approxis Require Import approxis.
From clutch.approxis.examples Require Import ElGamal_pubkey.
From clutch.approxis.examples Require valgroup_Zp valgroup_Zpx.

#[local] Definition rfc3526_id18 : N :=
  .

#[local] Definition p''' := (N.to_nat rfc3526_id18 - 3).
#[local] Definition p := S (S (S p''')).

Fact addSC p m : p = m+3 -> S (S (S (p - 3))) = p.
Proof. intros ->. rewrite Nat.add_sub Nat.add_comm //. Qed.
Fact p_eq_rfc3526_id18 : p = N.to_nat rfc3526_id18.
  unfold p, p'''.
  eapply (addSC _ (N.to_nat (rfc3526_id18 - 3))%N).
  replace 3%nat with (N.to_nat 3%N) ; [|auto].
  rewrite -N2Nat.inj_add.
  rewrite N.sub_add //.
Qed.

Section additive.
  Import valgroup_Zp.
  Definition EG_p := pk_ots_rnd_ddh (G:=@cg_p (N.to_nat rfc3526_id18)) (cgg:=cgg_p (N.to_nat rfc3526_id18)).
End additive.

Section multiplicative.
  Import valgroup_Zpx.

  Context `{rfc3526_id18_prime : is_true (prime.prime (N.to_nat rfc3526_id18))}.

  Fact p_prime : is_true (prime.prime p).
  Proof using rfc3526_id18_prime. by rewrite p_eq_rfc3526_id18. Qed.

  Definition EG_px := Eval hnf in pk_ots_rnd_ddh (G:=@cg_p _ p_prime) (cgg:=@cgg_p _ p_prime).

  Definition EG_DH := @ElGamal_DH_secure (vg_p _) (cgs_p _) _ (@cgg_p _ p_prime) (@cg_p _ p_prime).

  (* Set Printing Implicit.
     Check EG_DH. *)

End multiplicative.


